    /***
     * Carga los datos obtenidos atravez de buscar Hoja Zika.
     * @param hojaZika, Hoja Zika
     */
    public void cargarDatosZikaUI(HojaZikaDTO hojaZika) {

        this.mCodExp = hojaZika.getCodExpediente();
        this.mNumSeg = hojaZika.getNumHojaSeguimiento();
        this.findViewById(R.id.ibtCrearHoja).setEnabled(false);
        this.findViewById(R.id.ibtBuscarExp).setEnabled(false);

        ((EditText) this.findViewById(R.id.edtxtNombreApellido)).setText(hojaZika.getNomPaciente());

        ((EditText) this.findViewById(R.id.edtxtEstudioPacienteSeguimiento)).setText(hojaZika.getEstudioPaciente());

        ((EditText) this.findViewById(R.id.edtxtCodExpediente)).setText(String.valueOf(hojaZika.getCodExpediente()));

        ((EditText) this.findViewById(R.id.edtxtNumSeguimiento)).setText(String.valueOf(hojaZika.getNumHojaSeguimiento()));

        ((EditText) this.findViewById(R.id.edtxtFIS)).setText(hojaZika.getFis());

        ((EditText) this.findViewById(R.id.edtxtFIF)).setText(hojaZika.getFif());

        ((Spinner) this.findViewById(R.id.spnCategoria)).setSelection(mSeguimientoZikaActivity.adapterCat.getPosition((!StringUtils.isNullOrEmpty(hojaZika.getCategoria())) ?
                hojaZika.getCategoria().trim() : "Seleccione"));

        ((Spinner) this.findViewById(R.id.spnSintoma1)).setSelection(mSeguimientoZikaActivity.adapterSintomas.getPosition((!StringUtils.isNullOrEmpty(hojaZika.getSintomaInicial1())) ?
                hojaZika.getSintomaInicial1().trim() : "Seleccione"));

        ((Spinner) this.findViewById(R.id.spnSintoma2)).setSelection(mSeguimientoZikaActivity.adapterSintomas.getPosition((!StringUtils.isNullOrEmpty(hojaZika.getSintomaInicial2())) ?
                hojaZika.getSintomaInicial2().trim() : "Seleccione"));

        ((Spinner) this.findViewById(R.id.spnSintoma3)).setSelection(mSeguimientoZikaActivity.adapterSintomas.getPosition((!StringUtils.isNullOrEmpty(hojaZika.getSintomaInicial3())) ?
                hojaZika.getSintomaInicial3().trim() : "Seleccione"));

        ((Spinner) this.findViewById(R.id.spnSintoma4)).setSelection(mSeguimientoZikaActivity.adapterSintomas.getPosition((!StringUtils.isNullOrEmpty(hojaZika.getSintomaInicial4())) ?
                hojaZika.getSintomaInicial4().trim() : "Seleccione"));


        this.findViewById(R.id.ibtImprimir).setEnabled(true);

        if (hojaZika.getCerrado() == 'S') {
            this.findViewById(R.id.btnGuardar).setEnabled(false);
            this.findViewById(R.id.btnCerrarSeguimiento).setEnabled(false);
        }
        else {
            this.findViewById(R.id.btnGuardar).setEnabled(true);
            this.findViewById(R.id.btnCerrarSeguimiento).setEnabled(true);
        }

        if(hojaZika.getLstSeguimientoZika() != null && hojaZika.getLstSeguimientoZika().size() > 0){
            existeDetalle = true;
            //bandera para presentar el aviso de la fecha de seguimiento
            boolean presentaAviso = false;
            String mensajeAviso = "";

            for(SeguimientoZikaDTO seg : hojaZika.getLstSeguimientoZika()) {
                //cargnado los datos de seguimientos
                listaSegZika.add(seg);

                if(hojaZika.getCerrado() == 'N' && seg.getControlDia() == 1 && !StringUtils.isNullOrEmpty(seg.getFechaSeguimiento())){
                    try {
                        SimpleDateFormat sdf = new SimpleDateFormat("dd/MM/yyyy");
                        Date fSeg = sdf.parse(seg.getFechaSeguimiento());
                        String hoy = sdf.format(new Date());
                        Date dNow = sdf.parse(hoy);

                        long diferenciaEn_ms = dNow.getTime() - fSeg.getTime();
                        long dias = diferenciaEn_ms / (1000 * 60 * 60 * 24);

                        if(dias >= 5){
                            presentaAviso = true;
                            mensajeAviso = String.format(getResources().getString(R.string.msj_aviso_fecha_cierre_seguimiento), dias);
                        }

                    }catch (ParseException e) {
                        e.printStackTrace();
                        MensajesHelper.mostrarMensajeError(CONTEXT,
                                getResources().getString(R.string.msj_error_no_controlado), getResources().getString(R.string.app_name), null);
                    }
                }
            }

            if(presentaAviso) {
                MensajesHelper.mostrarMensajeInfo(CONTEXT, mensajeAviso, getResources().getString(R.string.app_name), null);
            }
        }

        ViewPager viewPager = (ViewPager) this.findViewById(R.id.vpDias);
        viewPager.setOffscreenPageLimit(1);
        viewPager.setAdapter(PlaceholderFragment.mPagerAdapter);


        viewPager.setVisibility(View.VISIBLE);
    }

