        private void obtenerSeguimientoZikaPdf(){
        /*Creando una tarea asincrona*/
            AsyncTask<Void, Void, Void> segZikaPdf = new AsyncTask<Void, Void, Void>() {
                private ProgressDialog PD;
                private ConnectivityManager CM = (ConnectivityManager) getActivity().getSystemService(Context.CONNECTIVITY_SERVICE);
                private NetworkInfo NET_INFO = CM.getActiveNetworkInfo();
                private SeguimientoZikaWS SEGZIKAWS = new SeguimientoZikaWS(getResources());

                @Override
                protected void onPreExecute() {
                    PD = new ProgressDialog(getActivity());
                    PD.setTitle(getResources().getString(R.string.title_obteniendo));
                    PD.setMessage(getResources().getString(R.string.msj_espere_por_favor));
                    PD.setCancelable(false);
                    PD.setIndeterminate(true);
                    PD.show();
                }

                @Override
                protected Void doInBackground(Void... params) {
                    if (NET_INFO != null && NET_INFO.isConnected())
                    {
                        RESPUESTA = SEGZIKAWS.getSeguimientoZikaPdf(mSeguimientoZikaActivity.mNumSeg);
                    }

                    return null;
                }

                @Override
                protected void onPostExecute(Void result){
                    PD.dismiss();

                    try {

                        File file = new File("/sdcard/SeguimientoZika.pdf");
                        FileOutputStream fos = new FileOutputStream(file);
                        String filepath = "/sdcard/SeguimientoZika.pdf";
                        OutputStream pdffos = new FileOutputStream(filepath);
                        pdffos.write(RESPUESTA);
                        pdffos.flush();
                        pdffos.close();


                        if (file.exists()) {
                            Uri path = Uri.fromFile(file);
                            Intent intent = new Intent(Intent.ACTION_VIEW);
                            intent.setDataAndType(path, "application/pdf");
                            intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
                            startActivity(intent);
                        }
                    }catch(Exception e){
                        e.printStackTrace();
                    }

                }
            };
            segZikaPdf.execute((Void[])null);

        }

