            public void cargarMedico(int position, ArrayAdapter<MedicosDTO> medicoAdapter) {
                SeguimientoZikaDTO seguimientoZika = obtenerNuevoSeguimientoPorDia(mPage);
                if (seguimientoZika != null) {
                    seguimientoZika.setUsuarioMedico((medicoAdapter.getItem(position).getIdMedico().intValue() > 0) ?
                            medicoAdapter.getItem(position).getIdMedico() : 0);
                } else if (medicoAdapter.getItem(position).getIdMedico().intValue() > 0) {
                    seguimientoZika = new SeguimientoZikaDTO();
                    seguimientoZika.setControlDia(mPage);
                    seguimientoZika.setUsuarioMedico(medicoAdapter.getItem(position).getIdMedico());
                    mSeguimientoZikaActivity.nuevaListaSegZika.add(seguimientoZika);
                }
            }

            public void cargarSupervisor(int position, ArrayAdapter<MedicosDTO> supervisorAdapter) {
                SeguimientoZikaDTO seguimientoZika = obtenerNuevoSeguimientoPorDia(mPage);
                if (seguimientoZika != null) {
                    seguimientoZika.setSupervisor((supervisorAdapter.getItem(position).getIdMedico().intValue() > 0) ?
                            supervisorAdapter.getItem(position).getIdMedico() : 0);
                } else if (supervisorAdapter.getItem(position).getIdMedico().intValue() > 0) {
                    seguimientoZika = new SeguimientoZikaDTO();
                    seguimientoZika.setControlDia(mPage);
                    seguimientoZika.setSupervisor(supervisorAdapter.getItem(position).getIdMedico());
                    mSeguimientoZikaActivity.nuevaListaSegZika.add(seguimientoZika);
                }
            }

