            public SeguimientoZikaDTO obtenerSeguimientoPorDia(int dia) {
                for (SeguimientoZikaDTO seguimientoZika : mSeguimientoZikaActivity.listaSegZika) {
                    if(seguimientoZika.getControlDia() != ' ' &&
                            Integer.parseInt(""+seguimientoZika.getControlDia()) == dia) {
                        return seguimientoZika;
                    }
                }
                return null;
            }

            public SeguimientoZikaDTO obtenerNuevoSeguimientoPorDia(int dia) {
                if(mSeguimientoZikaActivity.nuevaListaSegZika != null) {
                    for (SeguimientoZikaDTO seguimientoZika : mSeguimientoZikaActivity.nuevaListaSegZika) {
                        if (seguimientoZika.getControlDia() != ' ' &&
                                Integer.parseInt("" + seguimientoZika.getControlDia()) == dia) {
                            return seguimientoZika;
                        }
                    }
                }
                return null;
            }

            public void removerNuevoSeguimientoPorDia(int dia) {
                if(mSeguimientoZikaActivity.nuevaListaSegZika != null) {
                    for (SeguimientoZikaDTO seguimientoZika : mSeguimientoZikaActivity.nuevaListaSegZika) {
                        if (seguimientoZika.getControlDia() != ' ' &&
                                Integer.parseInt("" + seguimientoZika.getControlDia()) == dia) {
                            mSeguimientoZikaActivity.nuevaListaSegZika.remove(seguimientoZika);
                            return;
                        }
                    }
                }
            }

